import Header from './components/Header'
import Translator from './components/Translator';
import React, {useEffect, useState} from "react";

function App() {

  const [text, setText] = useState();
  const [transalate, setTranslate] = useState();

  const handleInputChange = (event) => {
  const { name, value } = event.target;
  setText(prevState => ({
    ...prevState,
    [name] : value
  }));
};

  return (
    <div className="App">

      {/* <header className="App-header">
        Saurian-English Translator
      </header> */}

      <Header/>


      <body className = "App-body">
        <p>Welcome to the saurian Translator! Here you can transalate sentances from English to Saurian and Saurian to English.</p>

        <input placeholder='Enter your text here' className = "Input-box" onChange={handleInputChange}></input>
        <button>TRANSLATE</button>

        <p>Translation:</p>
        <div className = "results">
          <Translator text = {text}/>
        </div>

      </body>

    </div>
  );
}

export default App;
